allow:
Bash(powershell -Command "
Write-Host ''正在下载 Google Cloud SDK 安装器...'' -ForegroundColor Green
$installerPath = ""$env:TEMP\\GoogleCloudSDKInstaller.exe""
try {
    \(New-Object Net.WebClient\).DownloadFile\(''https://dl.google.com/dl/cloudsdk/channels/rapid/GoogleCloudSDKInstaller.exe'', $installerPath\)
    Write-Host ''下载完成！启动安装器...'' -ForegroundColor Green
    Start-Process -FilePath $installerPath -Wait
    Write-Host ''安装完成！请重启PowerShell并运行: gcloud init'' -ForegroundColor Yellow
} catch {
    Write-Host ''下载失败，请手动访问：https://cloud.google.com/sdk/docs/install'' -ForegroundColor Red
    Write-Host $_.Exception.Message -ForegroundColor Red
}
")
